mapper.select:
SELECT * 
FROM rating 
WHERE 
    movie_id = #{movieId} 
    AND user_id = #{userId}

mapper.update:
UPDATE rating 
SET rating = #{rating}, comment = #{comment} 
WHERE 
    movie_id = #{movieId} 
    AND user_id = #{userId}

mapper.select:
SELECT COUNT(*) as rating_count, AVG(rating) as avg_rating 
FROM rating 
WHERE user_id = #{userId}

